import com.github.eerohele.DitaOtTask

task web(type: DitaOtTask) {
    input 'root.ditamap'
    transtype 'html5'
    filter 'web.ditaval'
}

task pdf(type: DitaOtTask) {
    input 'root.ditamap'
    transtype 'pdf'
    filter 'print.ditaval'

    properties {
      property name: 'args.rellinks', value: 'all'
    }
}
